import React, { useState } from 'react';
import axios from 'axios'; // Importez axios pour les requêtes HTTP
import '../components/styles/login.css';
import Navbar from '../components/Navbar';
import Input from '../components/input'; // Assurez-vous que le nom du fichier et du composant est correct
import Button1 from '../components/Button1';

function SignUp() {
    const [firstName, setFirstName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    async function signup(event) {
        event.preventDefault();

        try {
            const response = await axios.post('http://localhost:3001/users', {
                firstName,
                email,
                password,
                role : "user"
            });

            setSuccess('Inscription réussie !');
            setError('');
        } catch (err) {
            setError('Erreur lors de l\'inscription. Veuillez réessayer.');
            setSuccess('');
        }
    }

    return (
        <div className="all">
            <Navbar />
            <div className="page">
                <h1>Sign up</h1>
                <form className="inputs" onSubmit={signup}>
                    <Input
                        placeholder="First name"
                        value={firstName}
                        onChange={(e) => setFirstName(e.target.value)}
                    />
                    <Input
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <Input
                        placeholder="Password"
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <div className="button">
                        <Button1 text="Signup" type="submit" />
                    </div>
                </form>
                {error && <p className="error-message">{error}</p>}
                {success && <p className="success-message">{success}</p>}
                <div className="link">
                    <a href="#">I already have an account</a>
                </div>
            </div>
        </div>
    );
}

export default SignUp;
