import React, { useState } from 'react';
import '../components/styles/login.css';

import Navbar from '../components/Navbar';
import Input from '../components/input';
import Button1 from '../components/Button1';

function SignUp() {
    // État pour les champs du formulaire
    const [firstName, setFirstName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    // Gestion des changements dans les champs du formulaire
    const handleFirstNameChange = (event) => setFirstName(event.target.value);
    const handleEmailChange = (event) => setEmail(event.target.value);
    const handlePasswordChange = (event) => setPassword(event.target.value);

    // Fonction pour soumettre le formulaire
    const handleSubmit = async (event) => {
        event.preventDefault(); // Empêcher le comportement par défaut du formulaire

        // Créer l'objet utilisateur
        const user = {
            firstName,
            email,
            password,
            role: 'user' // Vous pouvez ajuster cela selon votre logique
        };

        try {
            const response = await fetch('http://localhost:3001/users', { // Remplacez par votre URL API
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(user),
            });

            if (response.ok) {
                const result = await response.json();
                console.log('Utilisateur enregistré:', result);
                // Rediriger ou afficher un message de succès
            } else {
                const error = await response.json();
                console.error('Erreur lors de l\'inscription:', error.message);
                // Afficher le message d'erreur
            }
        } catch (error) {
            console.error('Erreur de réseau:', error);
            // Afficher le message d'erreur de réseau
        }
    };

    return (
        <div className="all">
            <Navbar />
            <div className="page">
                <h1>Sign up</h1>
                <form className="inputsSignup" onSubmit={handleSubmit}>
                    <Input
                        placeholder={"First name"}
                        value={firstName}
                        onChange={handleFirstNameChange}
                    />
                    <Input
                        placeholder={"Email"}
                        value={email}
                        onChange={handleEmailChange}
                    />
                    <Input
                        placeholder={"Password"}
                        type="password"
                        value={password}
                        onChange={handlePasswordChange}
                    />
                    <div className="button">
                        <Button1 text="Signup" type="submit" />
                    </div>
                </form>
                <div className="link">
                    <a href="#">I already have an account</a>
                </div>
            </div>
        </div>
    );
}

export default SignUp;
