import React, { useState, useEffect } from 'react';
import Navbar from "../components/Navbar";
import Card from "../components/Card";

import c from "../media/c.png";
import python from '../media/python.png'
import javascript from '../media/javascript.png'
import work from '../media/work.png'

import '../components/styles/dashboard.css'

function Dashboard() {
    const [firstName, setFirstName] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        async function fetchUserData() {
            try {
                const response = await fetch('http://localhost:3001/users/profile', {
                    method: 'GET',
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`, // Assurez-vous que le jeton est stocké correctement
                        'Content-Type': 'application/json'
                    }
                });

                if (!response.ok) {
                    throw new Error('Failed to fetch user data');
                }

                const data = await response.json();
                setFirstName(data.firstName);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        }

        fetchUserData();
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div>
            <Navbar />
            <div className="pageDash">
                <h2 className="title">Hi {firstName}!</h2>
                <div className="cards">
                    <Card title="Javascript" image={javascript} link="Go back →"/>
                    <Card title="Python" image={python} link="Go back →"/>
                    <Card title="C" image={c} link="Go back →"/>
                </div>
                <div className="section">
                    <div className="left">
                        <img src={work} alt=""/>
                    </div>
                    <div className="right">
                        <h3 className="subtitle">Recommended</h3>
                        <div className="cards2">
                            <Card title="Python" image={python} link="Discover →"/>
                            <Card title="C" image={c} link="Discover →"/>
                            <Card title="C" image={c} link="Discover →"/>
                            <Card title="C" image={c} link="Discover →"/>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Dashboard;
