import React, { useState } from 'react';
import '../components/styles/login.css';
import Navbar from '../components/Navbar';
import Input from '../components/input'; // Assurez-vous que le nom du fichier et du composant est correct
import Button1 from '../components/Button1';

// Déplacez les appels à useState à l'intérieur de la fonction composant
function SignUp() {
    const [firstName, setFirstName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault(); // Empêcher le comportement par défaut du formulaire
        console.warn(firstName, email, password); // Affichage dans la console
        // Ajoutez ici votre logique pour envoyer les données à l'API
    };

    return (
        <div className="all">
            <Navbar />
            <div className="page">
                <h1>Sign up</h1>
                <form className="inputs" onSubmit={handleSubmit}>
                    <Input
                        placeholder="First name"
                        value={firstName}
                        onChange={(e) => setFirstName(e.target.value)}
                    />
                    <Input
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <Input
                        placeholder="Password"
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <div className="button">
                        <Button1 text="Signup" type="submit" />
                    </div>
                </form>
                <div className="link">
                    <a href="#">I already have an account</a>
                </div>
            </div>
        </div>
    );
}

export default SignUp;
